mapper.select:


  and exam_name like concat('%',
    #{examName}, '%')

  and exam_short_name like concat('%',
    #{examShortName}, '%')

  and exam_date = #{examDate}

  and exam_type_code = #{examTypeCode}

  and exam_mode_code = #{examModeCode}

  and grade_code = #{gradeCode}

  and school_year_name like
    concat('%',
    #{schoolYearName}, '%')

  and graduate_code = #{graduateCode}


order by order_num
